import React, { useState, useEffect } from "react";
import BoardView from "./BoardView";
import "../styles/Content.css";

function Content() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState({ title: "", description: "", status: "todo" });

  useEffect(() => {
    fetch("http://localhost:3001/tasks")
      .then((response) => response.json())
      .then((data) => setTasks(data))
      .catch((error) => console.error("Error al cargar datos:", error));
  }, []);

  const saveTasksToJSON = (updatedTasks) => {
    fetch("http://localhost:3001/tasks", {
      method: "POST", // O POST si lo prefieres
      body: JSON.stringify(updatedTasks),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((response) => response.json())
      .then(() => {
        setTasks(updatedTasks);
      })
      .catch((error) => console.error("Error al guardar datos:", error));
  };

  const addTask = () => {
    setTasks([...tasks, { ...newTask, id: Date.now() }]);
    setNewTask({ title: "", description: "", status: "todo" });
  };

  const editTask = (editedTask) => {
    const updatedTasks = tasks.map((task) =>
      task.id === editedTask.id ? editedTask : task
    );
    setTasks(updatedTasks);
    saveTasksToJSON(updatedTasks);
  };

  const deleteTask = (taskToDelete) => {
    const updatedTasks = tasks.filter((task) => task.id !== taskToDelete.id);
    setTasks(updatedTasks);
  };

  const moveTask = (task, newStatus) => {
    const updatedTasks = tasks.map((t) =>
      t.id === task.id ? { ...t, status: newStatus } : t
    );
    setTasks(updatedTasks);
  };

  const moveTaskBackToTodo = (task) => {
    moveTask(task, "todo"); // Moves the task back to "todo" status
  };

  return (
    <div className="content">
      <div className="content-container">
        <h2>Backlog</h2>
        <div>
          <input
            type="text"
            placeholder="Título de la tarea"
            value={newTask.title}
            onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
          />
          <textarea
            placeholder="Descripción de la tarea"
            value={newTask.description}
            onChange={(e) =>
              setNewTask({ ...newTask, description: e.target.value })
            }
          />
          <button onClick={addTask}>Añadir Tarea</button>
        </div>
        <div>
          <BoardView
            tasks={tasks}
            onEdit={editTask}
            onDelete={deleteTask}
            onMove={moveTask}
            onMoveBackToTodo={moveTaskBackToTodo} // Pass the new function
            newTask={newTask}
          />
        </div>
      </div>
    </div>
  );
}

export default Content;
